    private void moveToEBook(){
        ArrayList<Book> books=Common.getInstance().getBooks();
        if(books.size()>0) {
            String ebook_string="";
            for(Book theBook : books){
                if(ebook_string==""){
                    ebook_string=theBook.getUrl()+"_name_"+theBook.getName()+"_icon_"+theBook.getIcon();
                }
                else{
                    ebook_string=ebook_string+"_split_"+theBook.getUrl()+"_name_"+theBook.getName()+"_icon_"+theBook.getIcon();

                }
            }
            Intent intent = new Intent(getBaseContext(), SelectFileActivity.class).putExtra("title", "eBooks").putExtra("data", ebook_string);
            startActivity(intent);
        }
        else{
            Toast.makeText(getBaseContext(),"No eBook data",Toast.LENGTH_LONG).show();
        }
    }
